(function($){
	$.fn.extend({
		leanModal:function(options){
			var defaults={top:100,overlay:0.5,closeButton:null};
			var wrap= $("<div class='wrap'></div>");
			var overlay=$("<div id='lean_overlay'></div>");
			$("body").append(wrap);
			wrap.append(overlay);
			options=$.extend(defaults, options);
			return this.each(function() {
				var o=options;
				$(this).click(function(e){
					var modal_id=$(this).attr("href");
					$("body").css('overflow','hidden');
					wrap.css("display","block");
					overlay.click(function(){close_modal(modal_id);});
					$(o.closeButton).click(function(){close_modal(modal_id);});
					wrap.append($(modal_id));
					var modal_height=$(modal_id).outerHeight();
					var modal_width=$(modal_id).outerWidth();
					overlay.css("display","block");
					overlay.css("opacity", 0.8);
					//$('#lean_overlay').css("display":"block",opacity:0});
					$('#lean_overlay').fadeTo(200,o.overlay);
					$(modal_id).css("display","block")
					$(modal_id).css("position","absolute")
					$(modal_id).css("opacity",0)
					$(modal_id).css("z-index",11000)
					$(modal_id).css("left",50+"%")
					$(modal_id).css("margin-left",-(modal_width/2)+"px")
					$(modal_id).css("top",o.top+"px");
					$('#lean_overlay').height(wrap.height());
					$(modal_id).fadeTo(200,1);
					overlay.css("height",wrap[0].scrollHeight);
					e.preventDefault();	
				});
			});
		
			function close_modal(modal_id){
				wrap.fadeOut(200,function(){
					wrap.css("display","none");
					$(modal_id).css('display','none');
					$("body").css('overflow','auto');
				});
			}
		}
	});
	})(jQuery);